this.services.service('Forum', function(es, $q, parseElastic) {
  var _this = this;
  this.newPost = {};
  this.newPostActive = {};
  this.createPost = function(params) {
    params.createdAt = Date.now();
    params.asked = [];
    return es.index({
      index: 'bloom',
      type: 'post',
      realtime: true,
      refresh: true,
      body: params
    });
  };
  this.getPost = function(_id, getCommentCount) {
    var def;
    if (getCommentCount == null) {
      getCommentCount = true;
    }
    def = $q.defer();
    parseElastic(es.get({
      index: 'bloom',
      type: 'post',
      id: _id
    })).then(function(post) {
      if (getCommentCount) {
        return _this.getCommentCounts([_id]).then(function(counts) {
          return def.resolve(_.extend(post, {
            commentCount: counts[0].count
          }));
        });
      } else {
        return def.resolve(post);
      }
    });
    return def.promise;
  };
  this.getCommentCounts = function(posts) {
    return $q.all(posts.map(function(id) {
      return es.count({
        index: 'bloom',
        type: 'comment',
        realtime: true,
        q: "postId:" + id
      });
    }));
  };
  this.getPosts = function() {
    var def;
    def = $q.defer();
    parseElastic(es.search({
      type: 'post',
      realtime: true,
      size: 30,
      body: {},
      sort: 'createdAt:desc'
    })).then(function(posts) {
      return _this.getCommentCounts(_.pluck(posts, '_id')).then(function(counts) {
        return def.resolve(posts.map(function(post, index) {
          return _.extend(post, {
            commentCount: counts[index].count
          });
        }));
      });
    });
    return def.promise;
  };
  return this;
});

