this.bloom.service('Ask', function(es, parseElastic, $q, NotifSender, Notifs, $filter) {
  var _this = this;
  this.add = function(query, to, from) {
    var fullQuery, update;
    fullQuery = _.extend(angular.copy(query), {
      body: {
        script: 'ctx._source.asked += ask',
        params: {
          ask: {
            to: to,
            from: from,
            createdAt: Date.now()
          }
        }
      }
    });
    update = es.update(fullQuery);
    update.then(function() {
      if (!(__indexOf.call(to, '@') >= 0)) {
        return NotifSender.create(to, 'ask', {
          from: from,
          to: to,
          type: query.type,
          id: query.id
        });
      }
    });
    return update;
  };
  this.get = function(query) {
    var def;
    def = $q.defer();
    parseElastic(es.get(query)).then(function(item) {
      return def.resolve(item.asked);
    });
    return def.promise;
  };
  return this;
});

