this.services.service('Notifs', function(es, $q, parseElastic, Forum, Comments) {
  var _this = this;
  this.addMetadata = function(notif) {
    var addComment, addPost, def, fakeDef, filters;
    addPost = function(notif, postId) {
      var def;
      def = $q.defer();
      Forum.getPost(postId).then(function(post) {
        return def.resolve(_.extend(notif, {
          post: post
        }));
      });
      return def.promise;
    };
    addComment = function(notif, commentId) {
      var def;
      def = $q.defer();
      Comments.get(commentId).then(function(comment) {
        return def.resolve(_.extend(notif, {
          comment: comment
        }));
      });
      return def.promise;
    };
    fakeDef = $q.defer();
    filters = (function() {
      var _this = this;
      switch (notif.type) {
        case 'postComment':
          def = $q.defer();
          $q.all([addComment(notif, notif.data.commentId), addPost(notif, notif.data.postId)]).then(function() {
            return def.resolve(notif);
          });
          return def.promise;
        case 'commentReply':
          def = $q.defer();
          $q.all([addComment(notif, notif.data.commentId), addPost(notif, notif.data.postId)]).then(function() {
            return def.resolve(notif);
          });
          return def.promise;
        case 'commentUpvote':
          def = $q.defer();
          $q.all([addComment(notif, notif.data.commentId), addPost(notif, notif.data.postId)]).then(function() {
            return def.resolve(notif);
          });
          return def.promise;
        case 'ask':
          switch (notif.data.type) {
            case 'comment':
              return addComment(notif, notif.data.id);
            case 'post':
              return addPost(notif, notif.data.id);
          }
          break;
        default:
          return fakeDef.promise;
      }
    }).call(_this);
    fakeDef.resolve(notif);
    return filters;
  };
  this.get = function(to) {
    var defer;
    defer = $q.defer();
    parseElastic(es.search({
      index: 'bloom',
      type: 'notif',
      size: 20,
      sort: 'createdAt:desc',
      to: to
    })).then(function(list) {
      return $q.all(list.map(_this.addMetadata)).then(function(joinedList) {
        return defer.resolve(joinedList);
      });
    }, function() {
      return defer.resolve([]);
    });
    return defer.promise;
  };
  return this;
});

