this.comments.controller('CommentCtrl', function($scope, $timeout, Users, Session, Comments) {
  var _this = this;
  this.data = $scope.data;
  this.saveWith = $scope.saveWith;
  this.depth = +$scope.depth;
  this.maxDepth = +$scope.maxDepth;
  this.tooDeepToReply = this.depth <= this.maxDepth;
  this.replying = false;
  this.toolsOpen = false;
  Users.get(this.data.author).then(function(user) {
    return _this.user = user;
  });
  this.newContent = '';
  this.myCommentsThisSession = [];
  this.upvoted = _.contains(_.pluck(this.data.upvotes, 'author'), Session.me, this.askQuery = {
    index: 'bloom',
    type: 'comment',
    id: this.data._id
  });
  this.owner = Session.me === this.data.author;
  this.editing = false;
  this["delete"] = function() {
    if (confirm("Are you sure you want to delete your comment?\n\nThe message will be removed but any replies will be preserved.")) {
      _this.data.deleted = true;
      return Comments["delete"](_this.data._id);
    }
  };
  this.startEdit = function() {
    _this.editContent = _this.data.content;
    _this.editing = true;
    return $timeout(function() {
      return $scope.$broadcast('focus');
    });
  };
  this.update = function(content) {
    Comments.update(_this.data._id, content);
    _this.data.content = content;
    return _this.editing = false;
  };
  if (this.data.comments != null) {
    this.data.comments = Comments.sort(this.data.comments);
  }
  this.upvote = function() {
    if (_this.upvoted) {
      return;
    }
    _this.upvoted = true;
    _this.data.upvotes.push({
      author: Session.me,
      createdAt: Date.now()
    });
    $scope.animateUpvote();
    return Comments.upvote(Session.me, _this.data._id, _this.postId);
  };
  $scope.$watch((function() {
    return _this.replying;
  }), function(val, lastReplying) {
    if (!lastReplying && _this.replying) {
      return $timeout(function() {
        return $scope.$broadcast('focus');
      });
    }
  });
  this.add = function() {
    return Comments.addToComment(Session.me, _this.newContent, _this.data._id, $scope.saveWith).then(function(saved) {
      Comments.get(saved._id).then(function(comment) {
        var _base;
        ((_base = _this.data).comments || (_base.comments = [])).unshift(comment);
        return $scope.$emit('addedComment');
      });
      _this.replying = false;
      return _this.newContent = '';
    });
  };
  return this;
});

