this.comments.directive('comment', function($compile, $animate) {
  return {
    restrict: 'E',
    scope: {
      data: '=',
      depth: '=',
      maxDepth: '@',
      saveWith: '='
    },
    templateUrl: this.appRoot + 'comments/comment.html',
    controller: 'CommentCtrl as comment',
    compile: function(tElement, tAttr) {
      var compiledContents, contents;
      contents = tElement.contents().remove();
      compiledContents = null;
      return function(scope, el, attr) {
        if (!compiledContents) {
          compiledContents = $compile(contents);
        }
        compiledContents(scope, function(clone, scope) {
          return el.append(clone);
        });
        return scope.animateUpvote = function() {
          return $animate.addClass(el.find('.comment-vote-icon'), 'animate-upvote', function() {
            return console.log('animation is done!');
          });
        };
      };
    }
  };
});

