this.services.service('Comments', function(es, $http, $q, NotifSender, Forum, Session, parseElastic, BloomSettings) {
  var _this = this;
  window.client = es;
  this.sort = function(comments) {
    return _.sortBy(comments, function(c) {
      return -_this.rank(c);
    });
  };
  this.prefer = function(comments, preferred) {
    var isMine;
    isMine = function(comment) {
      return _.contains(preferred, comment._id);
    };
    return _.filter(comments, isMine).concat(_.reject(comments, isMine));
  };
  this.rank = function(comment) {
    var order;
    order = Math.log(Math.max(comment.upvotes.length, 1)) / Math.log(1.2);
    return order + comment.createdAt / 45000000;
  };
  this.toTree = function(data) {
    var dataMap, treeData;
    dataMap = data.reduce(function(map, node) {
      map[node._id] = node;
      return map;
    }, {});
    treeData = [];
    data.forEach(function(node) {
      var parent;
      parent = dataMap[node.parentCommentId];
      if (parent) {
        return (parent.comments || (parent.comments = [])).push(node);
      } else {
        return treeData.push(node);
      }
    });
    return treeData;
  };
  this.upvote = function(author, commentId, postId) {
    return es.update({
      index: 'bloom',
      type: 'comment',
      id: commentId,
      body: {
        script: 'ctx._source.upvotes += upvote',
        params: {
          upvote: {
            author: author,
            createdAt: Date.now()
          }
        }
      }
    }).then(function() {
      return _this.get(commentId).then(function(comment) {
        return NotifSender.create(comment.author, 'commentUpvote', {
          author: author,
          postId: postId,
          commentId: commentId
        });
      });
    });
  };
  this.addToPost = function(author, content, saveWith) {
    window.bs = BloomSettings;
    console.log('adding ', author, content, saveWith);
    var def, params;
    def = $q.defer();
    params = {
      save: _.extend({
        author: author,
        content: content
      }, saveWith),
      auth: {
        user: Session.me,
        token: BloomSettings.token
      }
    };
    $http.post("" + API + "/comments", {
      params: params
    }).then(function(res) {
      return def.resolve(res.data);
    });
    return def.promise;
  };
  this.addToComment = function(author, content, parentCommentId, saveWith) {
    var def, params;
    def = $q.defer();
    params = {
      save: _.extend({
        parentCommentId: parentCommentId,
        author: author,
        content: content,
        asked: [],
        upvotes: [],
        createdAt: Date.now()
      }, saveWith),
      auth: {
        user: Session.me,
        token: BloomSettings.token
      }
    };
    $http.post("" + API + "/comments", {
      params: params
    }).then(function(res) {
      return def.resolve(res.data);
    });
    return def.promise;
  };
  this.get = function(_id) {
    var def;
    def = $q.defer();
    $http.get("" + API + "/comments/" + _id).then(function(res) {
      return def.resolve(res.data);
    });
    return def.promise;
  };
  this.getWith = function(params) {
    var def, q;
    def = $q.defer();
    q = _.map(params, function(val, key) {
      return "" + key + ":" + val;
    }).join(', ');
    $http.get("" + API + "/comments", {
      params: {
        q: q
      }
    }).then(function(res) {
      console.log('resolving with', res.data);
      return def.resolve(res.data);
    });
    return def.promise;
  };
  return this;
});

