this.bloom.controller('NotifsCtrl', function($scope, NotifsState, Notifs, Users, Session, $timeout) {
  var _this = this;
  this.Session = Session;
  this.list = [];
  this.calculateUnread = function() {
    if (NotifsState.opened) {
      return 0;
    }
    return _this.unread = _this.list.filter(function(notif) {
      return notif.createdAt > Session.data.lastOpenedNotifs;
    }).length;
  };
  this.updateLastRead = function() {
    Session.data.lastOpenedNotifs = Date.now();
    return Users.updateLastOpened(Session.me);
  };
  this.refresh = function() {
    var processList;
    processList = function(list) {
      if (_this.list.length > 0 && _this.list[0].createdAt === list[0].createdAt) {
        $timeout(_this.refresh, 1000);
        return;
      }
      _this.list = list;
      _this.loaded = true;
      _this.calculateUnread();
      return $timeout(_this.refresh, 1000);
    };
    return Notifs.get(Session.me).then(processList, processList);
  };
  Session.load().then(function(data) {
    _this.lastOpened = Session.data.lastOpenedNotifs;
    return _this.refresh();
  });
  $scope.$watch((function() {
    return NotifsState.opened;
  }), function(opened, wasOpened) {
    if (wasOpened && !opened) {
      _this.updateLastRead();
    }
    if (!wasOpened && opened) {
      return _this.unread = 0;
    }
  });
  return this;
});

